import React, { useRef, useEffect } from 'react';
import WaveSurfer from 'wavesurfer.js';
import SpectrogramPlugin from 'wavesurfer.js/dist/plugin/wavesurfer.spectrogram.min.js';
import { createFFmpeg, fetchFile } from '@ffmpeg/ffmpeg';
const ffmpeg = createFFmpeg({ log: true });

function App() {
  const wavesurferRef = useRef(null);
  const waveformRef = useRef(null);

  useEffect(() => {
    wavesurferRef.current = WaveSurfer.create({
      container: waveformRef.current,
      waveColor: 'violet',
      progressColor: 'purple',
      backend: 'WebAudio',
      plugins: [SpectrogramPlugin.create({ container: '#spectrogram' })],
    });
  }, []);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    await ffmpeg.load();

    ffmpeg.FS('writeFile', file.name, await fetchFile(file));
    await ffmpeg.run('-i', file.name, '-ac', '1', 'output.wav');
    const data = ffmpeg.FS('readFile', 'output.wav');
    const url = URL.createObjectURL(new Blob([data.buffer], { type: 'audio/wav' }));

    wavesurferRef.current.load(url);
  };

  return (
    <div className="App">
      <input type="file" accept=".mp3" onChange={handleFileUpload} />
      <div ref={waveformRef}></div>
      <div id="spectrogram"></div>
    </div>
  );
}

export default App;
